mapper.select:
SELECT b.user_name studentName, b. NO studentNo, c.user_name teacherName, c.phone, a.`index`, a.id, a.student_id studentId, a.teacher_id teacherId, a.type, a.reason, a.time_leave timeLeave, a.time_back timeBack, a.state, a.author_state authorState, DATE_FORMAT( a.create_time, '%Y-%m-%d %H:%i:%S' ) createTime, a.remark1, a.remark2, a.remark3, a.remark4 FROM( SELECT @num :=@num + 1 AS `index`, t.* FROM clxx_apply t, (SELECT @num := 0) r ORDER BY create_time DESC ) a LEFT JOIN clxx_user b ON a.student_id = b. NO LEFT JOIN clxx_user c ON a.teacher_id = c. NO where 1=1
and a.id = #{clxxApply.id}
and a.id in (${clxxApply.ids})
and a.teacher_id = #{clxxApply.teacherId}
and c.phone LIKE CONCAT('%',#{clxxApply.phone},'%')
and a.student_id = #{clxxApply.studentId}
and b.user_name LIKE CONCAT('%',#{clxxApply.studentName},'%')
and c.user_name LIKE CONCAT('%',#{clxxApply.teacherName},'%')
and a.state = #{clxxApply.state}
and a.type = #{clxxApply.type}
and a.state != #{clxxApply.stateNull}
and DATE_FORMAT(a.time_leave,'%Y-%m-%d') = #{clxxApply.timeLeave}
and DATE_FORMAT(a.time_back,'%Y-%m-%d') = #{clxxApply.timeBack}
and DATE_FORMAT(a.time_leave,'%Y-%m') = #{clxxApply.yearMonth}
order by a.create_time desc
